mapper.select:
select * from
(select rowNum rNum,h_no,h_name, h_phone, h_title,h_room, h_room_ok , h_price_room, h_option, h_price_option, h_local, h_cont, h_file,
h_hit,h_date from
(select * from tbl_Hotel






		h_title like #{find_name}




		h_local like #{find_name}




		h_cont like #{find_name}


order by h_no desc))
where rNum>=#{startrow} and rNum<=#{endrow}

select count(h_no) from tbl_Hotel






		h_title like #{find_name}




		h_local like #{find_name}




		h_cont like #{find_name}
